import React from "react";
import myphoto from "../assets/myphoto.png";
import { useTypewriter } from "react-simple-typewriter";

const Home = () => {
  const spanText = useTypewriter({
    words: ["前端網頁開發者", "廣告媒體人", "愛冒險愛旅行", "更愛新奇事物!"],
    loop: Infinity,
    typeSpeed: 80,
    deleteSpeed: 60,
    delaySpeed: 1500,
  });
  return (
    <div className="home">
      <div className="img_container">
        <img src={myphoto} alt="個人照片" />
      </div>
      <div className="intro_container">
        <h2>Hi There, I'm</h2>
        <h1>Ching Yang</h1>
        <h3>
          我是楊晴晴 ·{" "}
          <span style={{ color: "#f77117", fontWeight: "bold" }}>
            {spanText}
          </span>
        </h3>
      </div>
    </div>
  );
};

export default Home;
