import React from "react";
// import myphoto from "../assets/myphoto.png";
import photo1 from "../assets/photo1.jpg";
import photo2 from "../assets/photo2.jpg";
import photo3 from "../assets/photo3.jpg";
import photo4 from "../assets/photo4.jpg";

import { Typewriter } from "react-simple-typewriter";
import Slider from "react-slick";

const Home = () => {
  const spanText = [
    " 前端網頁開發者!",
    " 廣告媒體人!",
    " 愛冒險愛旅行!",
    " 更愛新奇事物!",
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slideToShow: 3,
    slideToScroll: 3,
  };

  return (
    <div id="home">
      <div className="img_container">
        <Slider {...settings}>
          <div>
            <img src={photo1} alt="個人照片" width="10px" height="10px" />
          </div>
          <div>
            <img src={photo2} alt="個人照片" width="10px" height="10px" />
          </div>
          <div>
            <img src={photo3} alt="個人照片" width="10px" height="10px" />
          </div>
          <div>
            <img src={photo4} alt="個人照片" width="10px" height="10px" />
          </div>
        </Slider>
      </div>
      <div className="intro_container">
        <h1>I'M CHING YANG</h1>
        <div className="typewriter_container">
          <span
            style={{
              color: "rgb(13, 49, 87)",
              fontWeight: "bold",
              fontSize: "1.8rem",
            }}
          >
            {" "}
            我是楊晴晴 ·
            <Typewriter
              words={spanText}
              loop={Infinity}
              cursor
              cursorStyle="|"
              typeSpeed={80}
              deleteSpeed={60}
              delaySpeed={1500}
            />
          </span>
        </div>
      </div>
    </div>
  );
};

export default Home;
