import React from "react";
import myphoto from "../assets/myphoto.png";
import { Typewriter } from "react-simple-typewriter";
import { useTranslation } from 'react-i18next';

const Home = () => {
    const { t,i18n } = useTranslation();
  const spanText = [
    " 前端網頁開發者!",
    " 廣告媒體人!",
    " 愛冒險愛旅行!",
    " 更愛新奇事物!",
  ];
  console.info("翻譯的",t("name"))
    console.info("選的語言",i18n.language)

  return (
    <div id="home">
      <div className="img_container">
        <img src={myphoto} alt="" />
      </div>
      <div className="intro_container">
        <h1>
          I'M <br /> {t("name")}
        </h1>
        <div className="typewriter_container">
          <span
            style={{
              color: "rgb(13, 49, 87)",
              fontWeight: "bold",
              fontSize: "1.8rem",
            }}
          >
            {" "}
            我是楊晴晴 ·
            <Typewriter
              words={spanText}
              loop={Infinity}
              cursor
              cursorStyle="|"
              typeSpeed={80}
              deleteSpeed={60}
              delaySpeed={1500}
            />
          </span>
        </div>
      </div>
    </div>
  );
};

export default Home;
