    } else if (currentImage === photo2) {
      setCurrentImage(photo3);
    } else if (currentImage === photo3) {
      setCurrentImage(photo4);
    } else {
      setCurrentImage(photo1);
    }
  };

  return (
    <div id="home">
      <div
        className="img_container"
        style={{ backgroundImage: `url(${currentImage})` }}
      >
        {/* <img src={myphoto} alt="個人照片" /> */}
      </div>
