import myphoto from "../assets/myphoto.png";
import { useEffect, useRef } from "react";
import { Typewriter } from "react-simple-typewriter";

const Home = () => {
  const spanText = [
    "前端網頁開發者",
    "廣告媒體人",
    "愛冒險愛旅行",
    "更愛新奇事物!",
  ];
  return (
    <div className="home">
      <div className="img_container">
        <img src={myphoto} alt="個人照片" />
      </div>
      <div className="intro_container">
        <h2>Hi There, I'm</h2>
        <h1>Ching Yang</h1>
        <h3>
          我是楊晴晴 ·{" "}
          <span style={{ color: "#f77117", fontWeight: "bold" }}>
            <Typewriter
              words={spanText}
              loop={5}
              cursor
              cursorStyle="_"
              typeSpeed={70}
              deleteSpeed={50}
              delaySpeed={1000}
            />
          </span>
        </h3>
      </div>
    </div>
  );
};

export default Home;
